import React from 'react';
import './stylesheets/App.css';
import Curve from "./Curve";

let curves = [];
let k = 0;

class App extends React.Component {
	constructor(props) {
	  super(props);	
	  this.state = {k: k};
	}
	newCurve = () => {
		this.setState({k: k+1});
	}
	render(){
		return (<div>
					{[...[curves.map]]}
				<button onclick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
