let Animations = {
	morph: (curves, time /*time in seconds*/, fps /*frames per second*/,executewithin, executeafter = null) => {
		let k = 0;
		let tab1, tab2 = [];
		let nbf = time*(fps/1000);
		var maininterval = window.setInterval(function(){
			if(k <= nbf){
				if(k == 0){
					for(let i = 0; i < curve1.X.length; i++){
						tab1[i] = curve1.X[i];
						tab2[i] = curve1.Y[i];
					}
				}
				else{
					for(let i = 0; i < curve1.X.length; i++){
						//console.log((curve2.Y)[i] + (curve1.Y)[i])/2
						tab1[i] = k*(curve2.X[curve2.X.length - 1 - i] - curve1.X[i])/nbf + curve1.X[i];
						let ai = (curve1.Y[i] - curve2.Y[curve2.Y.length - 1 - i])/(curve1.X[i] - curve2.X[curve2.X.length - 1 - i]);
						let bi = curve1.Y[i] - ai*curve1.X[i];
						tab2[i] = ai*tab1[i] + bi;
					}
				}						
				//console.log(tab2);
				executewithin(tab1, tab2);
				//(new Curve2d(tab1, tab2, "#3A539B", 3)).draw();
				//console.log("Iteration number " + k);
			}
			else{
				window.clearInterval(maininterval);	
				executeafter();
				//tab2 = [];	
			}
			k++;
		}, (time/nbf));
		return maininterval;
	},
};

export default Animations;
