import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCut, faAngleDown, faSave, faAngleLeft, faAngleRight, faPencilAlt } from '@fortawesome/free-solid-svg-icons';
import Functions from './Library';
import './stylesheets/App.css';

let curves = [[], []];

class Curve2d{
	constructor(X, Y, color, width) {
		this.X = X;
		this.Y = Y;
		this.color = color;
		this.width = width;
	}
}

class FCurve extends React.Component{
	constructor(props) {
		super(props);

		this.state = { cEdit: 0, tempF: [ null, null, null, null ] };
	}
	componentDidMount(){
		window.MathJax.Hub.Queue(["Typeset", window.MathJax.Hub, "parameters"]);
	}
	componentDidUpdate(){
		this.input.focus();
		this.input.value = this.state.tempF[this.state.cEdit - 1];
	}
	previous = () => {
		this.setState({ cEdit: (this.state.cEdit - 1) % 5, tempF: this.state.tempF.map((v, i) => (i === this.state.cEdit - 1) ? this.input.value : v)});
		this.input.value = "";
	};
	next = () => {
		this.setState({ cEdit: (this.state.cEdit + 1) % 5, tempF: this.state.tempF.map((v, i) => (i === this.state.cEdit - 1) ? this.input.value : v)});
		this.input.value = "";
	};
	edit = () => {
		this.setState({ cEdit: 1 });
	};	
	save = () => {
		this.setState({ cEdit: 0, tempF: this.state.tempF.map((v, i) => (i === this.state.cEdit - 1) ? this.input.value : v)});
		this.setState({ cEdit: 0 });	
	};
	inputHandler = (e) => {
		if(e.which === 13) this.next();
	};
	render(){
		let cInputClass = "", placeholder = "";
		switch(this.state.cEdit){
			case 1: 
				cInputClass = "selected f";
				placeholder = "cos(sin(x))";
				break;
			case 2:
				cInputClass = "selected value";
				placeholder = "-1";
				break;
			case 3:
				cInputClass = "selected value";
				placeholder = "1";
				break;
			case 4: 	
				cInputClass = "selected value";
				placeholder = "25";
				break;
			default: 
				cInputClass = "f";
				break;
		}
		return (<span className="input function">
					<span>
						<button className="delete" onClick={() => {this.props.delete([0, this.props.cindex])}} style={{display: (this.state.cEdit) ? "none" : "inline"}}><FontAwesomeIcon icon={faCut}/></button>
						<button className="dropdown" style={{display: (this.state.cEdit) ? "none" : "inline"}}><FontAwesomeIcon icon={faAngleDown} size="lg"/></button>
						<button className="save" onClick={this.save} style={{display: (this.state.cEdit) ? "inline" : "none"}}><FontAwesomeIcon icon={faSave}/></button>
						<button className="previous" onClick={this.previous} style={{display: (this.state.cEdit > 1) ? "inline" : "none"}}><FontAwesomeIcon icon={faAngleLeft} size="lg"/></button>
						<label className="fx" style={{display: (this.state.cEdit < 2) ? "inline" : "none"}}>&#92;(f_{ this.props.cindex + 1 }(x)&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 2) ? "inline" : "none"}}>&#92;(a&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 3) ? "inline" : "none"}}>&#92;(b&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 4) ? "inline" : "none"}}>&#92;(n&#92;)</label>
						<input className={cInputClass} type="text" ref={(input) => { this.input = input; }} onKeyUp={this.inputHandler} placeholder="sin(cos(x) - 1)" onChange={()=>{}}/>
						<button className="next" onClick={this.next} style={{display: (this.state.cEdit) ? "inline" : "none"}}><FontAwesomeIcon icon={faAngleRight} size="lg"/></button>
						<button className="edit" onClick={this.edit} style={{display: (this.state.cEdit) ? "none" : "inline"}}><FontAwesomeIcon icon={faPencilAlt}/></button>
						<input className="jscolor" value="#3A539B" onChange={()=>{}}/>
					</span>
				</span>);
	}
}

