import React from 'react';
import './stylesheets/App.css';

let curves = [];

class FCurve extends React.Component{
	constructor(props) {
		super(props);

		this.state = { cEdit: null };
	}
	componentDidMount(){
		window.MathJax.Hub.Queue(["Typeset", window.MathJax.Hub, "parameters"]);
	}
	edit = () => {
		this.setState({ cEdit: 1 });
	}
	render(){
		return (<span className="input function">
					<span>
						<button className="delete" onClick={() => {this.props.delete(this.props.cindex)}}><i className = "fa fa-cut"></i></button>
						<button className="dropdown"><i className="fa fa-angle-down fa-lg"></i></button>
						<button className="save"><i className = "fa fa-save"></i></button>
						<button className="previous"><i className="fa fa-angle-left fa-lg"></i></button>
						<label className="fx">&#92;(f_{ this.props.cindex + 1 }(x)&#92;)</label>
						<label className="value">&#92;(a&#92;)</label>
						<label className="value">&#92;(b&#92;)</label>
						<label className="value">&#92;(n&#92;)</label>
						<input className={(this.state.cEdit) ? "selected f" : "f"} type="text" placeholder="sin(cos(x) - 1)" onChange={()=>{}}/>
						<button className="next"><i className="fa fa-angle-right fa-lg"></i></button>
						<button className="edit" onClick={this.edit}><i className = "fa fa-pencil-alt"></i></button>
						<input className="jscolor" value="#3A539B" onChange={()=>{}}/>
					</span>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
		super(props);	
		curves.push(Date.now());
		this.state = {curves: curves};
	}
	newCurve = () => {
		//var curve = <Curve cindex={curves.length} delete={this.delete} />;
		curves.push(Date.now());
		this.setState({curves: curves});
	}
	delete = (arg) => {
		curves.splice(arg, 1);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					<div>
						{curves.map((el, ind) => <FCurve key={ind} cindex={ind} delete={this.delete} />)}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
