import React from 'react';
import './stylesheets/App.css';

let curves = [];

class Curve extends React.Component{
	constructor(props) {
		super(props);
	}
	delete = (arg) => {
		curves.splice(this.props.cindex, 1);
	}
	render(){
		return (<span>
					<p>Curve {this.props.cindex} </p>
					<a href="#" onClick={() => this.delete(this)}> Delete </a>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
		super(props);	
		this.state = {curves: curves};
	}
	newCurve = () => {
		var curve = <span>\
					<p>Curve {this.props.cindex} </p>\
					<a href="#" onClick={() => this.delete(this)}> Delete </a>\
				</span>;
		curves.push(<Curve cindex={curves.length} />);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					<div>
						{curves}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
