import React from 'react';
import './stylesheets/App.css';
import Curve from "./Curve";

let curves = [];
let k = 0;

class App extends React.Component {
	constructor(props) {
	  super(props);

	  this.state = {k: k};
	}
	render(){
    	return (<div>
    				<p>{k}</p>
    				<div>{[...curves]}</div><br/>
    				<button onclick={this.setState({k: k++})}> Add Curve</button> 
    			</div>);
  }  
}

export default App;
