import React from 'react';
import './stylesheets/App.css';
import Curve from "./Curve";
var curves = [];
var k = 0;
class App extends React.Component {
  addCurve = () => {
  	k++;
  	curves.push(<Curve/>);
  }
  render(){
    return (<div>
    			<p>{{k}}</p>
    			<div>{[...curves]}</div><br/>
    			<button onclick={this.addCurve}>Add Curve</button>
    		</div>);
  }  
}

export default App;
