import React from 'react';
import './stylesheets/App.css';

let curves = [];

class FCurve extends React.Component{
	render(){
		return (<span class = "input function">
					<span>
						<a href="#" class="delete"><i class = "fa fa-cut"></i></a>
						<a href="#" class="dropdown"><i class="fa fa-angle-down fa-lg"></i></a>
						<a href="#" class="save"><i class = "fa fa-save"></i></a>
						<a href="#" class="previous"><i class="fa fa-angle-left fa-lg"></i></a>
						<label class="fx">&#92;(f_{1}(x)&#92;)</label>
						<input class="f" type="text" placeholder="sin(cos(x) - 1)"/>
						<a href="#" class="next"><i class="fa fa-angle-right fa-lg"></i></a>
						<a href="#" class="previous"><i class="fa fa-angle-left fa-lg"></i></a>
						<label class="value">&#92;(a&#92;)</label>
						<input class ="value" type="text" id="a" maxlength="8" value="" placeholder = "-5"/>
						<a href="#" class="next"><i class="fa fa-angle-right fa-lg"></i></a>
						<a href="#" class="previous"><i class="fa fa-angle-left fa-lg"></i></a>
						<label class="value">&#92;(b&#92;)</label>
						<input class ="value" type="text" id="b" maxlength="8" value="" placeholder = "5"/>
						<a href="#" class="next"><i class="fa fa-angle-right fa-lg"></i></a>
						<a href="#" class="previous"><i class="fa fa-angle-left fa-lg"></i></a>
						<label class="value">&#92;(n&#92;)</label>
						<input class ="value" type="text" id="n" maxlength="5" value="" placeholder = "100"/>
						<a href="#" class="next"><i class="fa fa-angle-right fa-lg"></i></a>
						<a href="#" class="edit"><i class = "fa fa-pencil-alt"></i></a>
						<input class="jscolor" value="#3A539B"/>
					</span>
				</span>);
	}
}

class Curve extends React.Component {
	render(){
		return (<span index={this.props.cindex}>
					<p>Curve {this.props.cindex} </p>
					<a href="#" onClick={() => {this.props.delete(this.props.cindex)}}> Delete </a>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
		super(props);	
		this.state = {curves: curves};
	}
	newCurve = () => {
		//var curve = <Curve cindex={curves.length} delete={this.delete} />;
		curves.push(Date.now());
		this.setState({curves: curves});
	}
	delete = (arg) => {
		curves.splice(arg, 1);
		console.log(curves);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					<div>
						{curves.map((el, ind) => <Curve key={ind} cindex={ind} delete={this.delete} />)}
						<Curve />
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
