import React from 'react';
import './stylesheets/App.css';

let curves = [];
let k = 1;

class FCurve extends React.Component{
	constructor(props) {
		super(props);

		this.state = { cEdit: null };
	}
	componentDidMount(){
		window.MathJax.Hub.Queue(["Typeset", window.MathJax.Hub, "parameters"]);
	}
	previous = () => {
		k = k - 1;
		console.log(Math.abs(k));
	};
	next = () => {

	};
	edit = () => {
		this.setState({ cEdit: 1 });
	}
	render(){
		let cInputClass = "";
		switch(this.state.cEdit){
			case 1: 
				cInputClass = "selected f";
				break;
			case 2: 
				cInputClass = "selected x";
				break;
			case 3:
				cInputClass = "selected y";
				break;
			default: 
				cInputClass = "f";
				break;
		}
		return (<span className="input function">
					<span>
						<button className="delete" onClick={() => {this.props.delete(this.props.cindex)}} style={{display: (this.state.cEdit) ? "none" : "inline"}}><i className = "fa fa-cut"></i></button>
						<button className="dropdown" style={{display: (this.state.cEdit) ? "none" : "inline"}}><i className="fa fa-angle-down fa-lg"></i></button>
						<button className="save" style={{display: (this.state.cEdit) ? "inline" : "none"}}><i className = "fa fa-save"></i></button>
						<button className="previous" onClick={this.previous} style={{display: (this.state.cEdit) ? "inline" : "none"}}><i className="fa fa-angle-left fa-lg"></i></button>
						<label className="fx">&#92;(f_{ this.props.cindex + 1 }(x)&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 2) ? "inline" : "none"}}>&#92;(a&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 3) ? "inline" : "none"}}>&#92;(b&#92;)</label>
						<label className="value" style={{display: (this.state.cEdit === 4) ? "inline" : "none"}}>&#92;(n&#92;)</label>
						<input className={cInputClass} type="text" placeholder="sin(cos(x) - 1)" onChange={()=>{}}/>
						<button className="next" onClick={this.next} style={{display: (this.state.cEdit) ? "inline" : "none"}}><i className="fa fa-angle-right fa-lg"></i></button>
						<button className="edit" onClick={this.edit} style={{display: (this.state.cEdit) ? "none" : "inline"}}><i className = "fa fa-pencil-alt"></i></button>
						<input className="jscolor" value="#3A539B" onChange={()=>{}}/>
					</span>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
		super(props);	
		curves.push(Date.now());
		this.state = {curves: curves};
	}
	newCurve = () => {
		//var curve = <Curve cindex={curves.length} delete={this.delete} />;
		curves.push(Date.now());
		this.setState({curves: curves});
	}
	delete = (arg) => {
		curves.splice(arg, 1);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					<div>
						{curves.map((el, ind) => <FCurve key={ind} cindex={ind} delete={this.delete} />)}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
