import React from 'react';
import './stylesheets/App.css';

let curves = [];

class FCurve extends React.Component{
	render(){
		return (<span className="input function">
					<span>
						<a href="#" className="delete" onClick={() => {this.props.delete(this.props.cindex)}}><i className = "fa fa-cut"></i></a>
						<a href="#" className="dropdown"><i className="fa fa-angle-down fa-lg"></i></a>
						<a href="#" className="save"><i className = "fa fa-save"></i></a>
						<a href="#" className="previous"><i className="fa fa-angle-left fa-lg"></i></a>
						<label className="fx">&#92;(f_{ this.props.cindex + 1 }(x)&#92;)</label>
						<input className="f" type="text" placeholder="sin(cos(x) - 1)" onChange={()=>{}}/>
						<a href="#" className="next"><i className="fa fa-angle-right fa-lg"></i></a>
						<a href="#" className="previous"><i className="fa fa-angle-left fa-lg"></i></a>
						<label className="value">&#92;(a&#92;)</label>
						<input className ="value" type="text" id="a" maxLength="8" value="" placeholder = "-5" onChange={()=>{}}/>
						<a href="#" className="next"><i className="fa fa-angle-right fa-lg"></i></a>
						<a href="#" className="previous"><i className="fa fa-angle-left fa-lg"></i></a>
						<label className="value">&#92;(b&#92;)</label>
						<input className ="value" type="text" id="b" maxLength="8" value="" placeholder = "5" onChange={()=>{}}/>
						<a href="#" className="next"><i className="fa fa-angle-right fa-lg"></i></a>
						<a href="#" className="previous"><i className="fa fa-angle-left fa-lg"></i></a>
						<label className="value">&#92;(n&#92;)</label>
						<input className ="value" type="text" id="n" maxLength="5" value="" placeholder = "100" onChange={()=>{}}/>
						<a href="#" className="next"><i className="fa fa-angle-right fa-lg"></i></a>
						<a href="#" className="edit"><i className = "fa fa-pencil-alt"></i></a>
						<input className="jscolor" value="#3A539B" onChange={()=>{}}/>
					</span>
				</span>);
	}
}

class Curve extends React.Component {
	render(){
		return (<span index={this.props.cindex}>
					<p>Curve {this.props.cindex} </p>
					<a href="#" onClick={() => {this.props.delete(this.props.cindex)}}> Delete </a>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
		super(props);	
		curves.push(Date.now());
		this.state = {curves: curves};
	}
	newCurve = () => {
		//var curve = <Curve cindex={curves.length} delete={this.delete} />;
		curves.push(Date.now());
		this.setState({curves: curves});
	}
	delete = (arg) => {
		curves.splice(arg, 1);
		this.setState({curves: curves});
	}
	componentDidUpdate(){
		console.log("Rendered");
	}
	render(){
		return (<div>
					<div>
						{curves.map((el, ind) => <FCurve key={ind} cindex={ind} delete={this.delete} />)}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
