import React from 'react';
import './stylesheets/App.css';


let curves = [<Curve key="0"/>];


class Curve extends React.Component {
	delete = () => {

	}
	render(){
		return (<span>
					<p>Curve 1 </p>
					<a href="#" onClick={this.delete}> Delete </a>
				</span>);
	}
}

class App extends React.Component {
	constructor(props) {
	  super(props);	
	  this.state = {curves: curves};
	}
	newCurve = () => {
		curves.push(<Curve key={(curves.length).toString()} />);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					<div>
						{curves}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}


export default App;
