import React from 'react';
import './stylesheets/App.css';
import Curve from "./Curve";

let curves = [];

class App extends React.Component {
	constructor(props) {
	  super(props);	
	  this.state = {curves: curves};
	}
	newCurve = () => {
		curves.push(<Curve />);
		console.log(curves);
		this.setState({curves: curves});
	}
	render(){
		return (<div>
					{[...[curves]]}
				<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
