import React from 'react';
import './stylesheets/App.css';

let curves = [];

class App extends React.Component {
	constructor(props) {
		super(props);	
		this.state = {curves: curves};
	}
	newCurve = () => {
		var curve = <span cindex={curves.length}>
					<p>Curve {curves.length} </p>
					<a href="#" onClick={() => this.delete(curve.props.cindex)}> Delete </a>
				</span>;
		curves.push(curve);
		this.setState({curves: curves});
	}
	delete = (arg) => {
		curves.splice(arg, 1);
		this.setState({curves: curves});
	}
	render(){
		console.log(curves);
		return (<div>
					<div>
						{curves}
					</div><br/>
					<button onClick={this.newCurve}> Add Curve </button>
				</div>);
	}
}

export default App;
